var e={id:"transcode-video-operation",name:"Transcode Video Operation",icon:"extension",description:"Transcode input file to HLS streams with multiple quality levels",overview:({file:e,folder_id:t})=>[{label:"File",text:e},{label:"Folder ID",text:t}],options:[{field:"file",name:"File",type:"text",meta:{interface:"input",options:{placeholder:"Directus Files Object"}},schema:{required:!0}},{field:"folder_id",name:"Folder ID",type:"text",meta:{interface:"input",options:{placeholder:"Enter Folder ID"},note:"Entry point for storing all transcoded files. If not provided, a new folder will be created."},schema:{required:!0}},{field:"playlist_reference_type",name:"Playlist Reference Type",type:"string",meta:{interface:"select-dropdown",options:{choices:[{text:"Directus File IDs (default)",value:"id"},{text:"Filename Disk (custom)",value:"filename_disk"}]},note:"How playlists should reference segments: File UIDs for files in Directus (/assets/:uuid) or Filename Disk for resources stored in a custom location (/stream/:filename_disk.m3u8)"},schema:{default_value:"id"}},{field:"qualities",name:"Quality Levels",type:"json",meta:{interface:"select-multiple-checkbox",options:{choices:[{text:"240p",value:"240p"},{text:"480p",value:"480p"},{text:"720p",value:"720p"},{text:"1080p",value:"1080p"},{text:"4K",value:"2160p"}]},note:"Select the quality levels to transcode the video to. The maximum quality level depends on the input video (no upscaling)."},schema:{default_value:["240p","480p","720p","1080p","2160p"]}},{field:"threads",name:"Thread Count",type:"integer",meta:{interface:"input",options:{placeholder:"1"},note:"Number of threads to use for encoding. Use 1 for single-threaded, or 0 to use all available CPU cores. Default: 1"},schema:{default_value:1}}]};export{e as default};
